export const payConfirmReducer = (state={}, action) => {

    switch (action.type){
        case "ORDER_PAY_CONFIRM_REQUEST":
            debugger;
            window.open(action.payload);
            return {paymentUrl: action.payload};
        case "ORDER_PAY_CONFIRM":
            debugger;

            window.localStorage.setItem('payConfim', JSON.stringify(action.payload));

            return {confirmPay: action.payload};
        default:
            return state;
    }

