export const payConfirmReducer = (state={}, action) => {

    switch (action.type){
        case 'PAY_CONFIRM_SUCCESS':
            return {confirmPay: action.payload};
        case 'PAY_CONFIRM_TOKEN':
            return {userInfo: action.payload};
        default:
            return state;
    }
};

